<script setup>

const props = defineProps({
  menu: {
    type: Object,
    required: true,
  },
});

const collapsed = ref(false);
const sideMenu = ref(false);

const setCollapseState = () => {
  if (window.scrollY > 50) {
    collapsed.value = true;
  } else {
    collapsed.value = false;
  }
};

const toggleSideMenu = () => {
  sideMenu.value = !sideMenu.value;
};

const closeSideMenu = () => {
  sideMenu.value = false;
};

onMounted(() => {
  window.addEventListener('scroll', setCollapseState);
});

onUnmounted(() => {
  window.removeEventListener('scroll', setCollapseState);
});
</script>

